<?phpfunction pf_itemgrid2_func( $atts ) {  extract( shortcode_atts( array(    'listingtype' => '',	'itemtype' => '',	'locationtype' => '',	'posts_in' => '',	'sortby' => 'ASC',	'orderby' => 'title',	'items' => 8,	'cols' => 4,	'features'=>array(),	'filters' => 'true',	'itemboxbg' => '',	'grid_layout_mode' => 'fitRows',	'featureditems'=>'',	'featureditemshide' => '',	'authormode'=>0,	'agentmode'=>0,	'author'=>'',	'manualargs' => '',	'hidden_output' => '',  ), $atts ) );$setup3_pointposttype_pt4_check = PFSAIssetControl('setup3_pointposttype_pt4_check','','1');$setup3_pointposttype_pt5_check = PFSAIssetControl('setup3_pointposttype_pt5_check','','1');$setup3_pointposttype_pt6_check = PFSAIssetControl('setup3_pointposttype_pt6_check','','1');$gridrandno_orj = PF_generate_random_string_ig();$gridrandno = 'pf_'.$gridrandno_orj;$listingtype_x = PFEX_extract_type_ig($listingtype);$itemtype_x = ($setup3_pointposttype_pt4_check == 1) ? PFEX_extract_type_ig($itemtype) : '' ;$locationtype_x = ($setup3_pointposttype_pt5_check == 1) ? PFEX_extract_type_ig($locationtype) : '' ;$features_x = ($setup3_pointposttype_pt6_check == 1) ? PFEX_extract_type_ig($features) : '' ;		$wpflistdata = "<div class='pflistgridview".$gridrandno_orj."-container pflistgridviewgr-container'>";				/* Get admin values */		$setup3_pointposttype_pt1 = PFSAIssetControl('setup3_pointposttype_pt1','','pfitemfinder');				/*Container & show check*/		$pfcontainerdiv = 'pflistgridview'.$gridrandno_orj.'';		$pfcontainershow = 'pflistgridviewshow'.$gridrandno_orj.'';							$pfgrid = '';		$pfheaderfilters = $filters;		$pfitemboxbg = '';				$pfgetdata = array();		$pfgetdata['sortby'] = $sortby;		$pfgetdata['orderby'] = $orderby;		$pfgetdata['posts_in'] = $posts_in;		$pfgetdata['items'] = $items;		$pfgetdata['cols'] = $cols;		$pfgetdata['filters'] = $filters;		$pfgetdata['itemboxbg'] = $itemboxbg;		$pfgetdata['grid_layout_mode'] = $grid_layout_mode;		$pfgetdata['listingtype'] = $listingtype_x;		$pfgetdata['itemtype'] = $itemtype_x;		$pfgetdata['locationtype'] = $locationtype_x;		$pfgetdata['features'] = $features_x;			$pfgetdata['featureditems'] = $featureditems;		$pfgetdata['featureditemshide'] = $featureditemshide;		$pfgetdata['authormode'] = $authormode;		$pfgetdata['agentmode'] = $agentmode;		$pfgetdata['author'] = $author;		$pfgetdata['manual_args'] = (!empty($manualargs))? maybe_unserialize(base64_decode($manualargs)): '';		$pfgetdata['hidden_output'] = (!empty($hidden_output))? maybe_unserialize(base64_decode($hidden_output)): '';		if($pfgetdata['cols'] != ''){$pfgrid = 'grid'.$pfgetdata['cols'];}						/*Get if sort/order/number values exist*/		if(isset($_GET['pfsearch-filter']) && $_GET['pfsearch-filter']!=''){$pfg_orderby = esc_attr($_GET['pfsearch-filter']);}else{$pfg_orderby = '';}		if(isset($_GET['pfsearch-filter-order']) && $_GET['pfsearch-filter-order']!=''){$pfg_order = esc_attr($_GET['pfsearch-filter-order']);}else{$pfg_order = '';}		if(isset($_GET['pfsearch-filter-number']) && $_GET['pfsearch-filter-number']!=''){$pfg_number = esc_attr($_GET['pfsearch-filter-number']);}else{$pfg_number = '';}		if(isset($_GET['pfsearch-filter-col']) && $_GET['pfsearch-filter-col']!=''){$pfgrid = esc_attr($_GET['pfsearch-filter-col']);}				if ( is_front_page() ) {	        $pfg_paged = (esc_sql(get_query_var('page'))) ? esc_sql(get_query_var('page')) : 1;   	    } else {	        $pfg_paged = (esc_sql(get_query_var('paged'))) ? esc_sql(get_query_var('paged')) : 1; 	    }		$args = array( 'post_type' => $setup3_pointposttype_pt1, 'post_status' => 'publish');				if($pfgetdata['posts_in']!=''){			$args['post__in'] = pfstring2BasicArray($pfgetdata['posts_in']);		}		if($pfgetdata['authormode'] != 0){			if (!empty($pfgetdata['author'])) {				$args['author'] = $pfgetdata['author'];			}		}						$setup22_searchresults_defaultppptype = PFSAIssetControl('setup22_searchresults_defaultppptype'