x_query']) > 0){					$args['tax_query'][(count($args['tax_query'])-1)]=					array(							'taxonomy' => $fieldtaxname_it,							'field' => 'id',							'terms' => $pfvalue_arr_it,							'operator' => 'IN'					);				}else{					$args['tax_query']=					array(						'relation' => 'AND',						array(							'taxonomy' => $fieldtaxname_it,							'field' => 'id',							'terms' => $pfvalue_arr_it,							'operator' => 'IN'						)					);				}				}			}			if($setup3_pointposttype_pt6_check == 1){				/* features type*/				if($pfgetdata['features'] != ''){				$pfvalue_arr_fe = PFGetArrayValues_ld($pfgetdata['features']);				$fieldtaxname_fe = 'pointfinderfeatures';				if(count($args['tax_query']) > 0){					$args['tax_query'][(count($args['tax_query'])-1)]=					array(							'taxonomy' => $fieldtaxname_fe,							'field' => 'id',							'terms' => $pfvalue_arr_fe,							'operator' => 'IN'					);				}else{					$args['tax_query']=					array(						'relation' => 'AND',						array(							'taxonomy' => $fieldtaxname_fe,							'field' => 'id',							'terms' => $pfvalue_arr_fe,							'operator' => 'IN'						)					);				}				}			}			if (empty($pfgetdata['itemboxbg'])) {				$pfgetdata['itemboxbg'] = PFSAIssetControl('setup22_searchresults_background2','','');			}						$pfitemboxbg = ' style="background-color:'.$pfgetdata['itemboxbg'].';"';			$pfheaderfilters = ($pfgetdata['filters']=='true') ? '' : 'false' ;						$meta_key_featured = 'webbupointfinder_item_featuredmarker';						if($pfg_orderby != ''){				if($pfg_orderby == 'date' || $pfg_orderby == 'title'){										$args['orderby'] = array('meta_value_num' => 'DESC' , $pfg_orderby => $pfg_order);					$args['meta_key'] = $meta_key_featured;										if (!empty($pfgetdata['manual_args'])) {						$args['meta_key'] = $meta_key_featured;						$pfgetdata['manual_args']['orderby'] = array('meta_value_num' => 'DESC' , $pfg_orderby => $pfg_order);					}				}else{										$args['meta_key']='webbupointfinder_item_'.$pfg_orderby;										if(PFIF_CheckFieldisNumeric_ld($pfg_orderby) == false){						$args['orderby']= array('meta_value' => $pfg_order);					}else{						$args['orderby']= array('meta_value_num' => $pfg_order);					}										if (!empty($pfgetdata['manual_args'])) {						$pfgetdata['manual_args']['meta_key']='webbupointfinder_item_'.$pfg_orderby;						if(PFIF_CheckFieldisNumeric_ld($pfg_orderby) == false){							$pfgetdata['manual_args']['orderby'] = array('meta_value' => $pfg_order);						}else{							$pfgetdata['manual_args']['orderby'] = array('meta_value_num' => $pfg_order);						}					}									}			}else{								if($pfgetdata['orderby'] != ''){					$args['meta_key'] = $meta_key_featured;					$args['orderby'] = array('meta_value_num' => 'DESC' , $pfgetdata['orderby'] => $pfgetdata['sortby']);				}else{					$args['meta_key'] = $meta_key_featured;					$args['orderby'] = array('meta_value_num' => 'DESC' , $setup22_searchresults_defaultsortbytype => $setup22_searchresults_defaultsorttype);				}			}									if($pfg_number != ''){				$args['posts_per_page'] = $pfg_number;				if (!empty($pfgetdata['manual_args'])) {					$pfgetdata['manual_args']['posts_per_page'] = $pfg_number;				}			}else{				if($pfgetdata['items'] != ''){					$args['posts_per_page'] = $pfgetdata['items'];				}else{					$args['posts_per_page'] = $setup22_searchresults_defaultppptype;				}			}						if($pfg_paged != ''){				$args['paged'] = $pfg_paged;				if (!empty($pfgetdata['manual_args'])) {					$pfgetdata['manual_args']['paged'] = $pfg_paged;				}			}				/*Featured items filter*/			if($pfgetdata['featureditems'] == 'yes' && $pfgetdata['featureditemshide'] != 'yes'){								if (!isset($args['meta_query'])) {					$args['meta_query'] = array();				}				if(count($args['meta_query']) > 0){					$args['meta_query'][(count($args['meta_query'])-1)] = array(						'key' => 'webbupointfinder_item_featuredmarker',				