			$wpflistdata .= "</form></div></div> ";/*Form End . List Data End*/					$wpflistdata .= "					<script type='text/javascript'>					(function($) {						'use strict'						$('.pflistgridview{$gridrandno_orj}-filters-right .pfgridlistit').click(function(){							$('input[name=pfsearch-filter-col]').val($(this).attr('data-pf-grid'));							$('#{$pfcontainershow}-form').submit();							});												$('.pflistgridview{$gridrandno_orj}-filters-left > li > label > select').change(function(){							$('#{$pfcontainershow}-form').submit();						});												$(function() {							var intervaltime = 0;							var makeitperfextpf = setInterval(function() {																															var layout_modes = {						        fitrows: 'fitRows',						        masonry: 'masonry'						        }						        $('.pflistgridview{$gridrandno_orj}-content').each(function(){						            var \$container = $(this);						            var \$thumbs = \$container.find('.pfitemlists-content-elements');						            var layout_mode = \$thumbs.attr('data-layout-mode');						            \$thumbs.isotope({						                itemSelector : '.isotope-item',						                layoutMode : (layout_modes[layout_mode]==undefined ? 'fitRows' : layout_modes[layout_mode])						            });						            						        });																								intervaltime++;								if (intervaltime == 5) {									clearInterval(makeitperfextpf);								}							}, 1000);						});						$('.pfButtons a').click(function() {							if($(this).attr('data-pf-link')){								$.prettyPhoto.open($(this).attr('data-pf-link'));							}						});					})(jQuery);					</script>";return $wpflistdata;}add_shortcode( 'pf_itemgrid2', 'pf_itemgrid2_func' ); ?>